mapper.select:
SELECT block_id, block_name, block_loc, block_ps FROM block
block_id = #{blockId}
block_name LIKE CONCAT('%', #{blockName}, '%')

SELECT block_id, block_name
FROM block
WHERE block_id = #{block_id}

mapper.update:
UPDATE block
SET block_name = #{blockName}, block_loc = #{blockLoc}, block_ps = #{blockPs}
WHERE block_id = #{blockId}
