mapper.select:
SELECT f.id, f.field_name, f.section_id, f.crop_id, c.crop_name
FROM field f LEFT JOIN section s ON f.section_id = s.id LEFT JOIN crop c ON c.id = f.crop_id

    
        f.field_name = #{fieldName}
    
    
        AND s.id = #{sectionId}
    
    
        AND c.id = #{cropId}

mapper.update:
UPDATE field
SET field_name = #{fieldName}, section_id = #{sectionId}, crop_id = #{crop.cropId}
WHERE id = #{id}

mapper.select:
SELECT field_id
FROM field
WHERE field_id = #{field_id}
