mapper.select:
SELECT field_id, field_name, block_id, crop_id, use_status, field_ps
FROM field

    
        field_name LIKE CONCAT('%', #{fieldName}, '%')
    
    
        AND block_id = #{block.blockId}
    
    
        AND crop_id = #{crop.cropId}
    
    
        AND use_status = #{useStatus}

SELECT field_id
FROM field
WHERE field_id = #{field_id}

mapper.update:
UPDATE field
SET field_name = #{fieldName}, block_id = #{block.blockId}, crop_id = #{crop.cropId},
use_status = #{useStatus}, field_ps = #{fieldPs}
WHERE field_id = #{fieldId}
