mapper.select:
SELECT terminal_id, terminal_type, use_status, termial_ps
FROM terminal

    
        terminal_id = #{terminalId}
    
    
        AND terminal_type LIKE CONCAT('%', #{terminalType}, '%')
    
    
        AND use_status = #{useStatus}

mapper.update:
UPDATE terminal
SET terminal_type = #{terminalType}, use_status = #{useStatus}, termial_ps = #{terminalPs}
WHERE terminal_id = #{terminalId}
