mapper.select:
SELECT vehicle_id, vehicle_type, block_id, use_status, vehicle_ps
FROM vehicle

    
        vehicle_id = #{vehicleId}
    
    
        AND vehicle_type LIKE CONCAT('%', #{vehicleType}, '%')
    
    
        AND block_id = #{block.blockId}
    
    
        AND use_status = #{useStatus}

mapper.update:
UPDATE vehicle
SET vehicle_type = #{vehicleType}, block_id = #{block.blockId},
use_status = #{useStatus}, vehicle_ps = #{vehiclePs}
WHERE vehicle_id = #{vehicleId}
